import eslint from '@eslint/js';
import tseslint from 'typescript-eslint';

export default tseslint.config(
  eslint.configs.recommended,
  ...tseslint.configs.recommended,
  {
    ignores: ['dist/**', 'node_modules/**', '.codanna/**', 'site/**'],
  },
  {
    files: ['**/*.ts'],
    languageOptions: {
      parser: tseslint.parser,
      parserOptions: {
        project: './tsconfig.json',
      },
    },
    rules: {
      '@typescript-eslint/no-unused-vars': ['error', { argsIgnorePattern: '^_', varsIgnorePattern: '^_', caughtErrorsIgnorePattern: '^_' }],
      '@typescript-eslint/explicit-function-return-type': 'off',
      '@typescript-eslint/no-explicit-any': 'warn',
      'no-console': 'off',
      'no-empty': ['error', { allowEmptyCatch: true }],
      'no-useless-escape': 'warn',
    },
  },
  {
    files: ['tests/**/*.ts', 'src/skills/server/test-*.ts'],
    rules: {
      '@typescript-eslint/no-explicit-any': 'off',
      '@typescript-eslint/no-unused-vars': 'off',
      'no-empty': 'off',
    },
    languageOptions: {
      parserOptions: {
        project: null,
      },
    },
  }
);

{
  "name": "madeinoz-osint-skill",
  "version": "1.3.0",
  "type": "module",
  "scripts": {
    "lint": "cd src/tools && bun run lint",
    "typecheck": "cd src/tools && bun run typecheck",
    "build": "cd src/tools && bun build ./index.ts --outdir ../../dist --target bun",
    "test": "cd src/tools && bun run test"
  },
  "dependencies": {
    "yaml": "^2.8.2"
  },
  "devDependencies": {
    "@types/bun": "latest",
    "typescript": "^5.7.2"
  },
  "engines": {
    "bun": ">=1.0.0"
  }
}

